#!/usr/bin/env node
import 'dotenv/config';
import module from 'node:module';
import { Logger } from '@graphql-hive/logger';
import { e as enableModuleCachingIfPossible, h as handleNodeWarnings, r as run } from './cli-BYCLUGLV.js';
import 'node:cluster';
import 'node:os';
import 'node:path';
import '@commander-js/extra-typings';
import '@graphql-yoga/render-graphiql';
import '@graphql-hive/gateway-runtime';
import '@graphql-hive/pubsub';
import '@graphql-mesh/utils';
import 'node:fs/promises';
import 'node:url';
import 'node:fs';
import 'node:http';
import 'node:https';
import '@graphql-tools/utils';
import '@opentelemetry/sdk-trace-base';
import '@graphql-tools/code-file-loader';
import '@graphql-tools/graphql-file-loader';
import '@graphql-tools/load';

globalThis.__VERSION__ = '2.1.2';
module.register("@graphql-hive/importer/hooks", {
  parentURL: (
    // @ts-ignore bob will complain when bundling for cjs
    import.meta.url
  ),
  data: {
    packedDepsPath: globalThis.__PACKED_DEPS_PATH__ || ""
  }
});
enableModuleCachingIfPossible();
handleNodeWarnings();
const log = new Logger();
run({ log }).catch((err) => {
  log.error(err);
  process.exit(1);
});
